import { Container } from "./styles"
import image from "../../assets/userTeste.png"
import { Link, useHistory } from "react-router-dom"
import { Button } from "@material-ui/core"


export const Header = ({ name, autenticado, setAutenticado }) => {

    const history = useHistory()

    const handleLogout = () => {
        setAutenticado(false)
        localStorage.clear()

    }

    return (
        <Container>
            <h1 onClick={() => history.push("/")} style={{cursor: "pointer"}}>KenzieHub<span style={{color: "pink", fontSize: "3rem"}}>.</span></h1>
            <Link to={"/user"}><h3><img src={image} alt={image} /> {name}</h3></Link>
            <Button style={{marginBottom: "1rem"}} variant="contained" color="secondary" onClick={handleLogout}>Logout</Button>
        </Container>  
    )
}
